import React, { useState, useMemo, useEffect } from 'react';
import BattenForm from './components/BattenForm';
import BattenChart from './components/BattenChart';
import ResultsDisplay from './components/ResultsDisplay';
import CompositeCalculator from './components/CompositeCalculator';
import { BattenInputs, SavedProfile } from './types';
import { calculateBattenBehavior } from './services/calculator';

const DEFAULT_INPUTS: BattenInputs = {
  testWeight: 2.0,
  testLength: 1000,
  self14: 5.0,
  self12: 8.0,
  self34: 4.0,
  weighted14: 20.0,
  weighted12: 33.0,
  weighted34: 16.0,
};

type ViewMode = 'dashboard' | 'composite';

const App: React.FC = () => {
  const [viewMode, setViewMode] = useState<ViewMode>('dashboard');
  const [inputs, setInputs] = useState<BattenInputs>(DEFAULT_INPUTS);
  const [profileName, setProfileName] = useState("");
  const [savedProfiles, setSavedProfiles] = useState<SavedProfile[]>([]);

  useEffect(() => {
    const saved = localStorage.getItem('batten_profiles');
    if (saved) {
      try { setSavedProfiles(JSON.parse(saved)); } catch (e) {}
    }
  }, []);

  const results = useMemo(() => calculateBattenBehavior(inputs), [inputs]);

  const handleSaveProfile = () => {
    if (!profileName.trim()) return;
    const newProfile: SavedProfile = {
      id: Date.now().toString(),
      name: profileName,
      inputs: { ...inputs },
      date: new Date().toLocaleDateString()
    };
    const updated = [newProfile, ...savedProfiles];
    setSavedProfiles(updated);
    localStorage.setItem('batten_profiles', JSON.stringify(updated));
    setProfileName("");
  };

  return (
    <div className="min-h-screen bg-slate-50 text-slate-900 pb-12 font-sans">
      {/* Header aumentato in altezza */}
      <header className="bg-white border-b border-slate-100 py-12 px-4">
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center justify-between gap-6">
          <div className="flex items-center gap-8 md:gap-16">
            <img 
              src="data:image/png;base64," 
              alt="POLIMI" 
              className="h-14 w-auto object-contain" 
            />
            <div className="h-12 w-px bg-slate-200 hidden md:block"></div>
            <img 
              src="data:image/jpeg;base64," 
              alt="MOITECH" 
              className="h-16 w-auto object-contain" 
            />
          </div>
          <div className="text-right hidden md:block">
            <p className="text-xs font-bold text-slate-400 uppercase tracking-widest">PoliMi Sailing Team &times; Moitech</p>
          </div>
        </div>
      </header>

      {/* Navigazione dedicata sotto l'header */}
      <nav className="bg-slate-900 text-white sticky top-0 z-40 shadow-xl">
        <div className="max-w-6xl mx-auto px-4 flex overflow-x-auto whitespace-nowrap">
          <button 
            onClick={() => setViewMode('dashboard')} 
            className={`px-6 py-4 text-xs font-black uppercase tracking-widest border-b-2 transition-all ${viewMode === 'dashboard' ? 'border-[#A12B2B] bg-white/5' : 'border-transparent text-slate-400 hover:text-white'}`}
          >
            Dashboard
          </button>
          <button 
            onClick={() => setViewMode('composite')} 
            className={`px-6 py-4 text-xs font-black uppercase tracking-widest border-b-2 transition-all ${viewMode === 'composite' ? 'border-[#A12B2B] bg-white/5' : 'border-transparent text-slate-400 hover:text-white'}`}
          >
            Stecca Composta (Forward)
          </button>
        </div>
      </nav>

      <main className="max-w-6xl mx-auto px-4 mt-8">
        {viewMode === 'dashboard' && (
          <div className="space-y-8 animate-in fade-in duration-500">
            <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
              <div className="lg:col-span-5 space-y-6">
                <div className="bg-white p-6 rounded-xl shadow-sm border border-slate-200">
                  <h3 className="text-[10px] font-black mb-4 text-slate-400 uppercase tracking-widest">Salva Sessione</h3>
                  <div className="flex gap-2">
                    <input type="text" placeholder="Nome profilo..." value={profileName} onChange={(e) => setProfileName(e.target.value)} className="flex-1 px-3 py-2 bg-slate-50 border border-slate-200 rounded-lg text-sm outline-none" />
                    <button onClick={handleSaveProfile} disabled={!profileName.trim()} className="px-4 py-2 bg-[#A12B2B] text-white rounded-lg text-xs font-bold uppercase hover:bg-[#8B2424]">Salva</button>
                  </div>
                </div>
                <BattenForm inputs={inputs} onChange={(u) => setInputs({...inputs, ...u})} />
              </div>
              <div className="lg:col-span-7 space-y-6">
                <ResultsDisplay results={results} />
                <BattenChart inputs={inputs} draftPos={results.draftPosition} />
              </div>
            </div>
          </div>
        )}

        {viewMode === 'composite' && (
          <div className="animate-in fade-in duration-500">
            <CompositeCalculator />
          </div>
        )}
      </main>

      <footer className="mt-12 text-center text-slate-400 text-[9px] border-t border-slate-200 pt-8 uppercase tracking-[0.2em] font-black">
        <p>&copy; {new Date().getFullYear()} BattenLab &bull; V 6.0 &bull; MOITECH &times; POLIMI SAILING TEAM</p>
      </footer>
    </div>
  );
};

export default App;